package college.dorm.servicetest;import java.util.List;import org.junit.Test;import org.junit.runner.RunWith;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.boot.test.context.SpringBootTest;import org.springframework.test.context.junit4.SpringRunner;import college.dorm.mapper.StudentMapper;import college.dorm.pojo.Student;import college.dorm.service.IHouseparentService;import college.dorm.service.IStudentService;import college.dorm.service.ex.ServiceException;@RunWith(SpringRunner.class)@SpringBootTestpublic class StudentServiceTest {	@Autowired	private IStudentService iss;	@Test	public void testAdd() {		try {			Integer i = iss.addNewStudent(123054, "Coco", 22, 320);		} catch (ServiceException e) {			System.out.println(e.getMessage());			e.printStackTrace();		}	}	@Test	public void testList() {		List<Student> list = iss.previewAllStudents(1);		for (Student student : list) {			System.out.println(student.toString());		}	}}